    private void txtB2KeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtB2KeyPressed
        if (evt.getKeyCode() == 10) {
            Integer to = Integer.valueOf(txtB1.getText()) + Integer.valueOf(txtB2.getText());
            if (to >= 28 && to <= 40) {
                lblTB.setText(String.valueOf(to));
                lblFS.setText(String.valueOf(to));
                lblAcreditacion.setText("Aprobado");
            } else if (to < 28 && to > 0) {
                lblTB.setText(String.valueOf(to));
            } else {
                lblMensaje.setText("Los valore ingresados estan fuera del rango deseado");
            }
        }
    }//GEN-LAST:event_txtB2KeyPressed

    private void txtS2KeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtS2KeyPressed
        if (evt.getKeyCode() == 10) {
            if (txtS1.getText().equals("")) {
                Integer to = Integer.valueOf(txtB1.getText()) + Integer.valueOf(txtS2.getText());
                if (to >= 28 && to <= 40) {
                    lblFS.setText(String.valueOf(to));
                    lblAcreditacion.setText("Aprobado");
                } else if (to < 28 && to > 0) {
                    lblFS.setText(String.valueOf(to));
                    lblAcreditacion.setText("Reprobado");
                } else {
                    txtS1.setText("");
                    lblFS.setText("");
                    lblAcreditacion.setText("");
                    lblMensaje.setText("Los valore ingresados estan fuera del rango deseado");
                }
            } else {
                Integer to = Integer.valueOf(txtS1.getText()) + Integer.valueOf(txtS2.getText());
                if (to >= 28 && to <= 40) {
                    lblFS.setText(String.valueOf(to));
                    lblAcreditacion.setText("Aprobado");
                } else if (to < 28 && to > 0) {
                    lblFS.setText(String.valueOf(to));
                    lblAcreditacion.setText("Reprobado");
                } else {
                    txtS1.setText("");
                    lblFS.setText("");
                    lblAcreditacion.setText("");
                    lblMensaje.setText("Los valore ingresados estan fuera del rango deseado");
                }
            }
        }
    }//GEN-LAST:event_txtS2KeyPressed

    private void txtA2KeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtA2KeyPressed
        if (evt.getKeyCode() == 10) {
            Integer aFinal = Integer.valueOf(txtA1.getText()) + Integer.valueOf(txtA2.getText());
            lblFA.setText(String.valueOf(aFinal));
            if (Integer.valueOf(lblFA.getText()) >= 28 && Integer.valueOf(lblFA.getText()) <= 40) {
                lblAFA.setText("Aprobado");
            } else if (Integer.valueOf(lblFA.getText()) < 28 && Integer.valueOf(lblFA.getText()) > 0) {
                lblAFA.setText("Reprobado");
            } else {
                txtA1.setText("");
                txtA2.setText("");
                lblFA.setText("");
                lblMensaje.setText("Los valore ingresados estan fuera del rango deseado");
            }
            if (lblAFA.getText().equals("Aprobado") && lblAcreditacion.getText().equals("Aprobado")) {
                lblPromo.setText("Aprobado");
            } else if (lblAFA.getText().equals("Aprobado") && lblAcreditacion.getText().equals("Reprobado")){
                lblPromo.setText("Reprobado");
            }else if (lblAFA.getText().equals("Reprobado") && lblAcreditacion.getText().equals("Aprobado")){
                lblPromo.setText("Reprobado");
            }else if (lblAFA.getText().equals("Reprobado") && lblAcreditacion.getText().equals("Reprobado")){
                lblPromo.setText("Reprobado");
            }
        }
    }//GEN-LAST:event_txtA2KeyPressed

