    public void GuardarComponenteEducativo() {
        String sFichero = "ComponenteEducativo.xml";
        File fichero = new File(sFichero);

        if (fichero.exists()) {
            String cedula = txtIdentificacion.getText();
            String nombreMa = txtNombreMateria.getText();
            String bim1 = txtB1.getText();
            String bim2 = txtB2.getText();
            String total = lblTB.getText();
            String sup1 = txtS1.getText();
            String sup2 = txtS2.getText();
            String nFinal = lblFS.getText();
            String acreditacion = lblAcreditacion.getText();
            String asis1 = txtA1.getText();
            String asis2 = txtA2.getText();
            String aFinal = lblFA.getText();
            String acreditacionA = lblAFA.getText();
            String promocion = lblPromo.getText();
            Metodos opera = new Metodos();
            opera.agregarNodoComponente(cedula, nombreMa, bim1, bim2, total, sup1, sup2, nFinal, acreditacion,
                    asis1, asis2, aFinal, acreditacionA, promocion);
        } else {
            String cedula = txtIdentificacion.getText();
            String nombreMa = txtNombreMateria.getText();
            String bim1 = txtB1.getText();
            String bim2 = txtB2.getText();
            String total = lblTB.getText();
            String sup1 = txtS1.getText();
            String sup2 = txtS2.getText();
            String nFinal = lblFS.getText();
            String acreditacion = lblAcreditacion.getText();
            String asis1 = txtA1.getText();
            String asis2 = txtA2.getText();
            String aFinal = lblFA.getText();
            String acreditacionA = lblAFA.getText();
            String promocion = lblPromo.getText();
            Metodos opera = new Metodos();
            opera.crearXmlComponente(cedula, nombreMa, bim1, bim2, total, sup1, sup2, nFinal, acreditacion,
                    asis1, asis2, aFinal, acreditacionA, promocion);
        }
        txtNombreMateria.setText("");
        txtB1.setText("");
        txtB2.setText("");
        lblTB.setText("");
        txtS1.setText("");
        txtS2.setText("");
        lblFS.setText("");
        lblAcreditacion.setText("");
        txtA1.setText("");
        txtA2.setText("");
        lblFA.setText("");
        lblAFA.setText("");
        lblPromo.setText("");

    }

    public void GuardarEstudiante() {
        String sFichero = "Estudiante.xml";
        File fichero = new File(sFichero);

        if (fichero.exists()) {
            String cedula = txtIdentificacion.getText();
            String estudiante = txtEstudiante.getText();
            String modalidad = txtModalidad.getText();
            String nivel = txtNivelAcademinco.getText();
            String periodo = txtPeriodo.getText();
            String titulacion = txtTitulacion.getText();

            Metodos opera = new Metodos();
            opera.agregarNodoEstudiante(estudiante, cedula, modalidad, nivel, periodo, titulacion);
        } else {
            String cedula = txtIdentificacion.getText();
            String estudiante = txtEstudiante.getText();
            String modalidad = txtModalidad.getText();
            String nivel = txtNivelAcademinco.getText();
            String periodo = txtPeriodo.getText();
            String titulacion = txtTitulacion.getText();

            Metodos opera = new Metodos();
            opera.crearXmlEstudiante(estudiante, cedula, modalidad, nivel, periodo, titulacion);
        }
        txtEstudiante.setText("");
        txtIdentificacion.setText("");
        txtModalidad.setText("");
        txtNivelAcademinco.setText("");
        txtPeriodo.setText("");
        txtTitulacion.setText("");
    }
